INSERT INTO `nytimes` VALUES ('15', '/2021/02/05/us/summer-school-covid.html', 'https://static01.nyt.com/images/2021/02/05/us/05virus-summerschool-1-sub/05virus-summerschool-1-sub-thumbStandard.jpg', 'Summer School Is a Hot Idea Right Now. Could It Work?', 'Numerous obstacles make extending the school year a tough proposition, but the Biden administration wants to put billions behind it as a way of offsetting pandemic-era learning losses.', '1');
INSERT INTO `nytimes` VALUES ('16', '/2021/02/05/us/tampa-superbowl-coronavirus.html', 'https://static01.nyt.com/images/2021/02/06/us/05sb-virus-tampa-1/merlin_183228846_ee9b9a57-150a-4221-9d34-fff49e8c617e-thumbStandard.jpg', 'How Does a City Host a Super Bowl in a Pandemic?', '“We’ll make the best of it,” Tampa’s mayor said. Plenty of people are preparing to party, with businesses eager for some tourism.', '0');
INSERT INTO `nytimes` VALUES ('17', '/2021/02/05/us/colleges-social-media-discipline.html', 'https://static01.nyt.com/images/2021/02/04/us/00colleges-socialmedia/00colleges-socialmedia-thumbStandard.jpg', 'Students Punished for ‘Vulgar’ Social Media Posts Are Fighting Back', 'A lawsuit against the University of Tennessee questions when schools can discipline students because of their online speech.', '0');
INSERT INTO `nytimes` VALUES ('18', '/2021/02/05/us/california-reopening-coronavirus.html', 'https://static01.nyt.com/images/2021/02/05/us/05reopeningcatoday-1/merlin_183072546_a3f47076-56fe-4fc0-9d25-3d48ffe3b5f9-thumbStandard.jpg', 'Take a Visual Tour Through California as It Reopens', 'Friday: Californians hope it’ll be their last time easing restrictions. See how it’s been going. Also: Capitol riot arrests; and SAG Award nominees.', '0');
INSERT INTO `nytimes` VALUES ('19', '/2021/02/06/nyregion/hudson-yards-nyc.html', 'https://static01.nyt.com/images/2021/02/02/nyregion/00nyvirus-hudsonyards1/00nyvirus-hudsonyards1-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'How the Pandemic Left the $25 Billion Hudson Yards Eerily Deserted', 'The largest private development in U.S. history has attracted marquee companies, but is struggling with unsold luxury condos and a mall barren of shoppers.', '0');
INSERT INTO `nytimes` VALUES ('20', '/2021/02/06/us/supreme-court-california-church-coronavirus.html', 'https://static01.nyt.com/images/2021/02/05/multimedia/05dc-scotus-churcs/merlin_182755137_d1905835-6128-42a0-8b6a-16d2faa750d7-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'Supreme Court Partly Backs Religious Challenge to California Virus Restrictions', 'The ruling followed a similar one in a case from New York and provided more evidence of a change in the court’s direction after the death of Justice Ruth Bader Ginsburg.', '0');
INSERT INTO `nytimes` VALUES ('21', '/2021/02/05/us/politics/biden-trump-intelligence-briefings.html', 'https://static01.nyt.com/images/2021/02/05/multimedia/05dc-biden-intel-breifing-trump/05dc-biden-intel-breifing-trump-thumbWide-v2.jpg?quality=75&auto=webp&disable=upscale', 'Biden Bars Trump From Receiving Intelligence Briefings, Citing ‘Erratic Behavior’', 'Mr. Biden said there was “no need” for former President Donald J. Trump to get the briefings, traditionally given to ex-presidents as a courtesy and to keep them informed if their advice is needed.', '0');
INSERT INTO `nytimes` VALUES ('22', '/2021/02/05/us/politics/biden-houthi-yemen-terrorist-designation.html', 'https://static01.nyt.com/images/2021/02/05/lens/05dc-houthi-photo/merlin_182819595_9505f65c-0647-4a05-a6c0-c5c6a0477729-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'Biden Reverses Trump Terrorist Designation for Houthis in Yemen', 'Critics of the Trump administration action said the penalties would punish the millions of starving people in Yemen more than the Houthi rebels.', '0');
INSERT INTO `nytimes` VALUES ('23', '/2021/02/05/us/politics/majorie-taylor-greene-republican-party.html', 'https://static01.nyt.com/images/2021/02/05/multimedia/05dc-greene-freed/05dc-greene-freed-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'Exiled From Committees, Greene Says She Is ‘Freed’ to Push Republicans to the Right', 'Representative Marjorie Taylor Greene warned that the House’s vote only left her with more time to insist on Republicans’ allegiance to former President Donald J. Trump.', '0');
INSERT INTO `nytimes` VALUES ('24', '/2021/02/05/us/politics/cudd-texas-florist-mexico.html', 'https://static01.nyt.com/images/2021/02/03/multimedia/03xp-cudd/03xp-cudd-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'Judge Says Florist Charged in Capitol Riot May Travel to Mexico', 'Jenny Cudd, who is charged with participating in the Jan. 6 siege, said she had planned a four-day “work-related bonding retreat” in the Riviera Maya with employees and their spouses.', '0');
INSERT INTO `nytimes` VALUES ('25', '/2021/02/05/us/politics/fbi-intelligence-domestic-terrorism.html', 'https://static01.nyt.com/images/2021/02/05/us/politics/05dc-intel-1/05dc-intel-1-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'U.S. Will Examine Giving F.B.I. More Resources to Counter Domestic Extremism', 'The issue of violent extremist groups in the United States has come to the top of the agenda since a mob of far-right groups stormed the Capitol on Jan. 6.', '0');
INSERT INTO `nytimes` VALUES ('26', '/2021/02/05/us/summer-school-covid.html', 'https://static01.nyt.com/images/2021/02/05/us/05virus-summerschool-1-sub/merlin_180816747_4948f1e0-b26b-40e1-9d52-0d051308e5e4-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'Summer School Is a Hot Idea Right Now. Could It Work?', 'Numerous obstacles make extending the school year a tough proposition, but the Biden administration wants to put billions behind it as a way of offsetting pandemic-era learning losses.', '0');
INSERT INTO `nytimes` VALUES ('27', '/2021/02/05/us/air-force-base-security-breach.html', 'https://static01.nyt.com/images/2021/02/05/lens/05xp-intruder-photo2/05xp-intruder-photo2-thumbWide.jpg?quality=75&auto=webp&disable=upscale', 'Intruder Boards a Military Plane at Joint Base Andrews', 'An unarmed man was given a summons for trespassing after he gained access to the base on Thursday and boarded an aircraft typically used by senior government and military leaders, officials said.', '0');
INSERT INTO `nytimes` VALUES ('28', '/2021/02/05/us/coronavirus-today.html', 'https://static01.nyt.com/images/2021/03/02/multimedia/02coronavirus-nl/02coronavirus-nl-thumbWide.png?quality=75&auto=webp&disable=upscale', 'Coronavirus Briefing: What Happened Today', 'Should television shows incorporate the pandemic into shows or not?', '0');

-- ----------------------------
-- Table structure for nytimes_details
-- ----------------------------
DROP TABLE IF EXISTS `nytimes_details`;
CREATE TABLE `nytimes_details` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `news_id` int(11) NOT NULL,
  `figure` text,
  `p_list` text,
  `alt` varchar(500) DEFAULT '',
  `src` varchar(500) DEFAULT '',
  `local_src` varchar(500) DEFAULT '',
